mapper.select:
SELECT product_code, product_name, description, price, filename
FROM product
WHERE product_code = #{product_code}

mapper.update:
UPDATE product
SET product_name=#{product_name}
	,price=#{price}
	,description=#{description}
	,filename=#{filename}
WHERE product_code=#{product_code}

mapper.select:
SELECT filename
FROM product
WHERE product_code=#{product_code}
